#Find all rows where any numeric variable is less than zero
cereal2 %>%
  rowwise() %>%
  mutate(random_values = runif(1, min = -1, max = 1), .after = name) %>%
  filter(if_any(where(is.numeric), ~ .x < 0)) %>%
  gt() %>% 
    tab_options(container.height = '500px') %>%
    gt_theme_538() 
